mapper.select:
select * from e_paper ep
where
1=1

    AND ep.owner = #{owner}


    AND ep.code = #{code}


    AND ep.title = #{title}


    AND ep.del = #{del}


    AND ep.level = #{level}


    AND ep.typeId = #{typeId}

SELECT * FROM ( SELECT ep.id, ep.code, ep.del, ep.imgUrl, ep.`level` , ep.owner, ep.remark, ep.`time`, ep.title, ep.typeId , epa.delivery, epa.duration, epa.`end`, epa.isCamera, epa.iscopy , epa.isDoRname, epa.isOpenForever, epa.ispaste, epa.isrname, epa.isShowResult , epa.`join`, epa.`leave`,epa.passMark, epa.noMark, epa.page, epa.submit , epa.status, epa.`start`, epa.personal, epq2.questionNum, b.`type` FROM e_paper ep LEFT JOIN e_paper_attribute epa ON ep.id = epa.paper_id LEFT JOIN ( SELECT epq.paper_id, COUNT(1) AS questionNum FROM e_paper_question epq GROUP BY epq.paper_id ) epq2 ON epq2.paper_id = ep.id LEFT JOIN ( SELECT ept.id, ept.name AS `type` FROM e_user eu, e_paper_type ept WHERE ept.user_id = eu.id AND eu.id = 1001 ) b ON ep.typeId = b.id ) c WHERE c.owner= #{owner}
AND c.del = #{del}
and ( c.code LIKE concat('%',#{key},'%') or c.title LIKE concat('%',#{key},'%') or c.type LIKE concat('%',#{key},'%') )
AND c.personal = #{personal}
