mapper.select:
SELECT a.*,eqt.name `type` FROM ( SELECT eq.id, eq.title, eq.`type` AS typeId, eq.`time`, eq.`from`, eq.origin, CASE eq.`level` WHEN 1 THEN '简单' WHEN 2 THEN '一般' WHEN 3 THEN '中等' WHEN 4 THEN '较难' WHEN 5 THEN '极难' END `levelStr` ,eq.`level`,eq.createtype, eq.owner, eq.site, eq.del, eqa.content, eqa.correct, eqa.`options` FROM e_question eq LEFT JOIN e_question_attribute eqa ON eq.contentid = eqa.id) a, e_question_type eqt WHERE eqt.id = a.typeId AND a.id IN ( SELECT euq.question_id FROM e_user_questions euq WHERE euq.user_id = #{owner}
and euq.del=#{del}
and euq.question_id=#{questionId}
)
and ( a.content LIKE concat('%',#{key},'%') or a.title LIKE concat('%',#{key},'%') or a.type LIKE concat('%',#{key},'%') )
